import React, {Component} from 'react'
import './App.css';

import {sampleText} from './sampleText'

class App extends Component {

  state = {
    text : sampleText
  }

  render(){
    return (
      <div className='container'>
        <div className='row'>
          <div className='.col-lg-6'>
            <textarea defaultValue={this.state.text} className='form-control' rows='35'/>
          </div>
          <div className='.col-lg-6'>
            <div>
              {sampleText}
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default App;
